INSERT INTO `infoshop` (`id`, `shopName`, `email`, `address`, `phoneNumber`, `logo`, `img_desc`, `introShop`, `created_at`, `updated_at`) VALUES
(4, 'T&N', 'tnshop@gmail.com', 'Công viên phần mềm Quang QTSC Building 1, Trung, P Q.12, Thành phố Hồ Chí Minh', '0386134526', '1671530365-logo.png', '57735-img_desc.jpg,58344-img_desc.jpg,55936-img_desc.jpg,54862-img_desc.jpg,50195-img_desc.jpg,50345-img_desc.jpg', '<p><strong>T&amp;N</strong>&nbsp;l&agrave; thương hiệu mới người ti&ecirc;u d&ugrave;ng y&ecirc;u th&iacute;ch thời trang của Việt Nam trong thời gian qua.</p>\r\n\r\n<p>Được th&agrave;nh lập v&agrave;o năm 2022 với kh&aacute;t khao của 1 đội ngũ y&ecirc;u chuộng thời trang v&agrave; sở hữu gu thẩm mỹ tuyệt vời, nh&oacute;m đ&atilde; quyết định mở ra Doanh nghiệp của ri&ecirc;ng m&igrave;nh trong ng&agrave;nh thời trang. Phong c&aacute;ch thời trang của m&igrave;nh l&agrave; nguồn cảm hứng khiến &ocirc;ng lựa chọn ph&acirc;n phối c&aacute;c thương hiệu thời trang quốc tế nổi tiếng tại thị trường thời trang Việt Nam.<br />\r\n&nbsp;</p>\r\n\r\n<p>Hiện <strong>T&amp;N</strong> International đang l&agrave; ng&ocirc;i nh&agrave; chung của những thương hiệu đ&igrave;nh đ&aacute;m thế giới:&nbsp;<strong>Kappa, Ecko Unltd, Superga, Replay, Staple</strong>&nbsp;được b&aacute;n tại hệ thống gần 50 cửa h&agrave;ng rộng khắp to&agrave;n quốc được nhận biết r&otilde; r&agrave;ng th&ocirc;ng qua kiến tr&uacute;c m&aacute;i v&ograve;m gạch cổ, kh&ocirc;ng gian mua sắm hiện đại, tho&aacute;ng rộng, địa điểm dễ t&igrave;m kiếm v&agrave; website b&aacute;n h&agrave;ng thương mại điện tử.</p>\r\n\r\n<p><strong>T&amp;N</strong> kh&ocirc;ng chỉ thổi l&agrave;n gi&oacute; thời trang thế giới đa dạng m&agrave; c&ograve;n mang đến một dịch vụ phục vụ kh&aacute;ch h&agrave;ng với phương ch&acirc;m<strong>&nbsp;&quot;You&#39;re Happy - We&#39;re Happy&quot;</strong>&nbsp;(Bạn h&agrave;i l&ograve;ng - Ch&uacute;ng t&ocirc;i hạnh ph&uacute;c). Để <strong>T&amp;N&nbsp;</strong>xứng tầm l&agrave; điểm đến mua sắm số 1 d&agrave;nh cho những người y&ecirc;u thời trang.<br />\r\n&nbsp;</p>\r\n\r\n<p>Trụ sở ch&iacute;nh của <strong>J&amp;N</strong>&nbsp;tọa lạc tại&nbsp;<strong>C&ocirc;ng vi&ecirc;n phần mềm Quang QTSC Building 1, Trung, P Q.12, Th&agrave;nh phố Hồ Ch&iacute; Minh.</strong>&nbsp;Với 1 trong những gi&aacute; trị kinh doanh l&agrave; mang lại HẠNH PH&Uacute;C cho mọi người, ch&uacute;ng t&ocirc;i mong muốn m&ocirc;i trường l&agrave;m việc tại <strong>T&amp;N</strong> International phải l&agrave; một gia đ&igrave;nh thứ hai của tất cả mọi nh&acirc;n vi&ecirc;n. Nơi họ cảm thấy b&igrave;nh an, hạnh ph&uacute;c v&agrave; đam m&ecirc; cống hiến để c&ugrave;ng <strong>T&amp;N</strong>&nbsp;lan tỏa tinh thần thời trang cao cấp v&agrave; biến Việt Nam th&agrave;nh điểm đến hấp dẫn tr&ecirc;n bản đồ thời trang thế giới.</p>\r\n\r\n<p>Việc đầu tư x&acirc;y dựng Trung t&acirc;m kho vận theo ti&ecirc;u chuẩn quốc tế, với diện t&iacute;ch l&ecirc;n tới 10.000 m&eacute;t vu&ocirc;ng để lưu giữ v&agrave; ph&acirc;n phối h&agrave;ng h&oacute;a. <strong>T&amp;N</strong> một lần nữa muốn thể hiện được sự chuy&ecirc;n nghiệp cũng như cam kết x&acirc;y dựng v&agrave; ph&aacute;t triển thị trường b&aacute;n lẻ thời trang tại Việt Nam.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>T&amp;N</strong> l&agrave; &quot;s&acirc;n chơi&quot; d&agrave;nh cho những con người ch&aacute;y bỏng đam m&ecirc; thời trang với văn h&oacute;a l&agrave;m việc &quot;mở&quot;, khuyến kh&iacute;ch sự đổi mới, chấp nhận th&aacute;ch thức. Nếu bạn y&ecirc;u th&iacute;ch những thử th&aacute;ch mới, h&atilde;y c&ugrave;ng gia nhập đội ngũ <strong>T&amp;N</strong>&nbsp;để được&nbsp;<strong>&quot;ch&aacute;y hết m&igrave;nh&quot; v&agrave; c&ugrave;ng nhau tạo n&ecirc;n điều kh&aacute;c biệt.</strong></p>', '2022-12-20 09:58:58', '2022-12-20 16:48:30');
